(function() {
  var Website, http, nodeStatic;
  http = require('http');
  nodeStatic = require('node-static/lib/node-static');
  Website = (function() {
    function Website() {}
    Website.prototype.DEFAULT_PORT = 8080;
    Website.prototype.port = process.env.PORT || Website.DEFAULT_PORT;
    Website.prototype.start = function() {
      this.website_server = http.createServer(function(request, response) {
        var file;
        file = new nodeStatic.Server('./public', {
          cache: false
        });
        return request.addListener('end', function() {
          return file.serve(request, response);
        });
      });
      return this.website_server.listen(8080, function() {
        return console.log("Hello I am listening.");
      });
    };
    return Website;
  })();
  exports.Website = Website;
}).call(this);
